CREATE OR REPLACE FUNCTION biomasschp_boiler_calculation_design()
RETURNS TRIGGER AS
$BODY$
BEGIN
----------------------实现字段c_sum:总和,的计算1-----------------------------------
  IF OLD.c_water_content_received_design != NEW.c_water_content_received_design OR OLD.c_carbon_content_received_design != NEW.c_carbon_content_received_design OR OLD.c_hydrogen_content_received_design != NEW.c_hydrogen_content_received_design OR OLD.c_oxygen_content_received_design != NEW.c_oxygen_content_received_design OR OLD.c_nitrogen_content_design != NEW.c_nitrogen_content_design OR OLD.c_sulfur_content_received_design != NEW.c_sulfur_content_received_design OR OLD.c_ash_content_received_design != NEW.c_ash_content_received_design THEN
     update biomasschp_boiler_calculation set 

     c_sum_design=NEW.c_carbon_content_received_design+NEW.c_hydrogen_content_received_design+NEW.c_oxygen_content_received_design+NEW.c_nitrogen_content_design+NEW.c_sulfur_content_received_design+NEW.c_ash_content_received_design+NEW.c_water_content_received_design
     where plan_id=NEW.plan_id;

  ELSIF (OLD.c_water_content_received_design ISNULL OR OLD.c_ash_content_received_design ISNULL OR OLD.c_sulfur_content_received_design ISNULL OR OLD.c_nitrogen_content_design ISNULL OR OLD.c_oxygen_content_received_design ISNULL OR OLD.c_hydrogen_content_received_design ISNULL OR OLD.c_carbon_content_received_design ISNULL) AND NEW.c_water_content_received_design NOTNULL AND NEW.c_ash_content_received_design NOTNULL AND NEW.c_sulfur_content_received_design NOTNULL AND NEW.c_nitrogen_content_design NOTNULL AND NEW.c_oxygen_content_received_design NOTNULL AND NEW.c_hydrogen_content_received_design NOTNULL AND NEW.c_carbon_content_received_design NOTNULL THEN
     update biomasschp_boiler_calculation set 

     c_sum_design=NEW.c_carbon_content_received_design+NEW.c_hydrogen_content_received_design+NEW.c_oxygen_content_received_design+NEW.c_nitrogen_content_design+NEW.c_sulfur_content_received_design+NEW.c_ash_content_received_design+NEW.c_water_content_received_design
     where plan_id=NEW.plan_id;

  END IF;
----------------------实现字段c_daf:干燥无灰基挥发分,的计算2-----------------------------------
  IF OLD.c_water_content_received_design != NEW.c_water_content_received_design OR OLD.c_base_volatile_obtained_design != NEW.c_base_volatile_obtained_design OR OLD.c_carbon_content_received_design != NEW.c_carbon_content_received_design OR OLD.c_hydrogen_content_received_design != NEW.c_hydrogen_content_received_design OR OLD.c_oxygen_content_received_design != NEW.c_oxygen_content_received_design OR OLD.c_nitrogen_content_design != NEW.c_nitrogen_content_design OR OLD.c_sulfur_content_received_design != NEW.c_sulfur_content_received_design OR OLD.c_ash_content_received_design != NEW.c_ash_content_received_design THEN
     update biomasschp_boiler_calculation set 

     c_daf_design=NEW.c_base_volatile_obtained_design/((NEW.c_carbon_content_received_design+NEW.c_hydrogen_content_received_design+NEW.c_oxygen_content_received_design+NEW.c_nitrogen_content_design+NEW.c_sulfur_content_received_design+NEW.c_ash_content_received_design+NEW.c_water_content_received_design)-NEW.c_water_content_received_design-NEW.c_ash_content_received_design)*100
     where plan_id=NEW.plan_id;

  ELSIF (OLD.c_base_volatile_obtained_design ISNULL OR OLD.c_water_content_received_design ISNULL OR OLD.c_ash_content_received_design ISNULL OR OLD.c_sulfur_content_received_design ISNULL OR OLD.c_nitrogen_content_design ISNULL OR OLD.c_oxygen_content_received_design ISNULL OR OLD.c_hydrogen_content_received_design ISNULL OR OLD.c_carbon_content_received_design ISNULL) AND NEW.c_base_volatile_obtained_design NOTNULL AND NEW.c_water_content_received_design NOTNULL AND NEW.c_ash_content_received_design NOTNULL AND NEW.c_sulfur_content_received_design NOTNULL AND NEW.c_nitrogen_content_design NOTNULL AND NEW.c_oxygen_content_received_design NOTNULL AND NEW.c_hydrogen_content_received_design NOTNULL AND NEW.c_carbon_content_received_design NOTNULL THEN
     update biomasschp_boiler_calculation set 

     c_daf_design=NEW.c_base_volatile_obtained_design/((NEW.c_carbon_content_received_design+NEW.c_hydrogen_content_received_design+NEW.c_oxygen_content_received_design+NEW.c_nitrogen_content_design+NEW.c_sulfur_content_received_design+NEW.c_ash_content_received_design+NEW.c_water_content_received_design)-NEW.c_water_content_received_design-NEW.c_ash_content_received_design)*100
     where plan_id=NEW.plan_id;

  END IF;
----------------------实现字段c_base_heat_received_calculation:收到基低位发热量计算得到,的计算3-----------------------------------
  IF OLD.c_base_heat_received_user_design != NEW.c_base_heat_received_user_design THEN
     update biomasschp_boiler_calculation set 

     c_base_heat_received_calculation_design=NEW.c_base_heat_received_user_design/4.1868
     where plan_id=NEW.plan_id;

  ELSIF (OLD.c_base_heat_received_user_design ISNULL) AND NEW.c_base_heat_received_user_design NOTNULL THEN
     update biomasschp_boiler_calculation set 

     c_base_heat_received_calculation_design=NEW.c_base_heat_received_user_design/4.1868
     where plan_id=NEW.plan_id;

  END IF;
----------------------实现字段c_low_calorific_value_estimation:低位发热量估算,的计算4-----------------------------------
  IF OLD.c_water_content_received_design != NEW.c_water_content_received_design OR OLD.c_carbon_content_received_design != NEW.c_carbon_content_received_design OR OLD.c_hydrogen_content_received_design != NEW.c_hydrogen_content_received_design OR OLD.c_oxygen_content_received_design != NEW.c_oxygen_content_received_design OR OLD.c_sulfur_content_received_design != NEW.c_sulfur_content_received_design THEN
     update biomasschp_boiler_calculation set 

     c_low_calorific_value_estimation_design=339*NEW.c_carbon_content_received_design+1030*NEW.c_hydrogen_content_received_design-109*(NEW.c_oxygen_content_received_design-NEW.c_sulfur_content_received_design)-25*NEW.c_water_content_received_design
     where plan_id=NEW.plan_id;

  ELSIF (OLD.c_water_content_received_design ISNULL OR OLD.c_sulfur_content_received_design ISNULL OR OLD.c_oxygen_content_received_design ISNULL OR OLD.c_hydrogen_content_received_design ISNULL OR OLD.c_carbon_content_received_design ISNULL) AND NEW.c_water_content_received_design NOTNULL AND NEW.c_sulfur_content_received_design NOTNULL AND NEW.c_oxygen_content_received_design NOTNULL AND NEW.c_hydrogen_content_received_design NOTNULL AND NEW.c_carbon_content_received_design NOTNULL THEN
     update biomasschp_boiler_calculation set 

     c_low_calorific_value_estimation_design=339*NEW.c_carbon_content_received_design+1030*NEW.c_hydrogen_content_received_design-109*(NEW.c_oxygen_content_received_design-NEW.c_sulfur_content_received_design)-25*NEW.c_water_content_received_design
     where plan_id=NEW.plan_id;

  END IF;
----------------------实现字段c_high_calorific_value_estimation:高位发热量估算,的计算5-----------------------------------
  IF OLD.c_carbon_content_received_design != NEW.c_carbon_content_received_design OR OLD.c_hydrogen_content_received_design != NEW.c_hydrogen_content_received_design OR OLD.c_oxygen_content_received_design != NEW.c_oxygen_content_received_design OR OLD.c_sulfur_content_received_design != NEW.c_sulfur_content_received_design THEN
     update biomasschp_boiler_calculation set 

     c_high_calorific_value_estimation_design=339*NEW.c_carbon_content_received_design+1256*NEW.c_hydrogen_content_received_design-109*(NEW.c_oxygen_content_received_design-NEW.c_sulfur_content_received_design)
     where plan_id=NEW.plan_id;

  ELSIF (OLD.c_sulfur_content_received_design ISNULL OR OLD.c_oxygen_content_received_design ISNULL OR OLD.c_hydrogen_content_received_design ISNULL OR OLD.c_carbon_content_received_design ISNULL) AND NEW.c_sulfur_content_received_design NOTNULL AND NEW.c_oxygen_content_received_design NOTNULL AND NEW.c_hydrogen_content_received_design NOTNULL AND NEW.c_carbon_content_received_design NOTNULL THEN
     update biomasschp_boiler_calculation set 

     c_high_calorific_value_estimation_design=339*NEW.c_carbon_content_received_design+1256*NEW.c_hydrogen_content_received_design-109*(NEW.c_oxygen_content_received_design-NEW.c_sulfur_content_received_design)
     where plan_id=NEW.plan_id;

  END IF;
----------------------实现字段f_boiler_pressure:锅筒压力,的计算6-----------------------------------
  IF OLD.f_steam_pressure_design != NEW.f_steam_pressure_design THEN
     update biomasschp_boiler_calculation set 

     f_boiler_pressure_design=NEW.f_steam_pressure_design*1.1
     where plan_id=NEW.plan_id;

  ELSIF (OLD.f_steam_pressure_design ISNULL) AND NEW.f_steam_pressure_design NOTNULL THEN
     update biomasschp_boiler_calculation set 

     f_boiler_pressure_design=NEW.f_steam_pressure_design*1.1
     where plan_id=NEW.plan_id;

  END IF;
----------------------实现字段f_boiler_consumption:锅炉燃料消耗量,的计算7-----------------------------------
  IF OLD.c_base_heat_received_user_design != NEW.c_base_heat_received_user_design OR OLD.f_steam_flow_design != NEW.f_steam_flow_design OR OLD.f_steam_enthalpy_design != NEW.f_steam_enthalpy_design OR OLD.f_saturated_water_enthalpy_design != NEW.f_saturated_water_enthalpy_design OR OLD.f_water_enthalpy_design != NEW.f_water_enthalpy_design OR OLD.f_boiler_efficiency_design != NEW.f_boiler_efficiency_design OR OLD.f_blowdown_rate_design != NEW.f_blowdown_rate_design THEN
     update biomasschp_boiler_calculation set 

     f_boiler_consumption_design=NEW.f_steam_flow_design*1000/(NEW.f_boiler_efficiency_design/100)*((NEW.f_steam_enthalpy_design-NEW.f_water_enthalpy_design)+(NEW.f_blowdown_rate_design/100)*(NEW.f_saturated_water_enthalpy_design-NEW.f_water_enthalpy_design))/NEW.c_base_heat_received_user_design
     where plan_id=NEW.plan_id;

  ELSIF (OLD.f_blowdown_rate_design ISNULL OR OLD.f_boiler_efficiency_design ISNULL OR OLD.f_water_enthalpy_design ISNULL OR OLD.f_saturated_water_enthalpy_design ISNULL OR OLD.f_steam_enthalpy_design ISNULL OR OLD.f_steam_flow_design ISNULL OR OLD.c_base_heat_received_user_design ISNULL) AND NEW.f_blowdown_rate_design NOTNULL AND NEW.f_boiler_efficiency_design NOTNULL AND NEW.f_water_enthalpy_design NOTNULL AND NEW.f_saturated_water_enthalpy_design NOTNULL AND NEW.f_steam_enthalpy_design NOTNULL AND NEW.f_steam_flow_design NOTNULL AND NEW.c_base_heat_received_user_design NOTNULL THEN
     update biomasschp_boiler_calculation set 

     f_boiler_consumption_design=NEW.f_steam_flow_design*1000/(NEW.f_boiler_efficiency_design/100)*((NEW.f_steam_enthalpy_design-NEW.f_water_enthalpy_design)+(NEW.f_blowdown_rate_design/100)*(NEW.f_saturated_water_enthalpy_design-NEW.f_water_enthalpy_design))/NEW.c_base_heat_received_user_design
     where plan_id=NEW.plan_id;

  END IF;
----------------------实现字段f_calculation_consumption:计算燃料消耗量,的计算8-----------------------------------
  IF OLD.c_base_heat_received_user_design != NEW.c_base_heat_received_user_design OR OLD.f_steam_flow_design != NEW.f_steam_flow_design OR OLD.f_steam_enthalpy_design != NEW.f_steam_enthalpy_design OR OLD.f_saturated_water_enthalpy_design != NEW.f_saturated_water_enthalpy_design OR OLD.f_water_enthalpy_design != NEW.f_water_enthalpy_design OR OLD.f_boiler_efficiency_design != NEW.f_boiler_efficiency_design OR OLD.f_unburned_loss_design != NEW.f_unburned_loss_design OR OLD.f_blowdown_rate_design != NEW.f_blowdown_rate_design THEN
     update biomasschp_boiler_calculation set 

     f_calculation_consumption_design=(NEW.f_steam_flow_design*1000/(NEW.f_boiler_efficiency_design/100)*((NEW.f_steam_enthalpy_design-NEW.f_water_enthalpy_design)+(NEW.f_blowdown_rate_design/100)*(NEW.f_saturated_water_enthalpy_design-NEW.f_water_enthalpy_design))/NEW.c_base_heat_received_user_design)*(1-(NEW.f_unburned_loss_design/100))
     where plan_id=NEW.plan_id;

  ELSIF (OLD.f_blowdown_rate_design ISNULL OR OLD.f_unburned_loss_design ISNULL OR OLD.f_boiler_efficiency_design ISNULL OR OLD.f_water_enthalpy_design ISNULL OR OLD.f_saturated_water_enthalpy_design ISNULL OR OLD.f_steam_enthalpy_design ISNULL OR OLD.f_steam_flow_design ISNULL OR OLD.c_base_heat_received_user_design ISNULL) AND NEW.f_blowdown_rate_design NOTNULL AND NEW.f_unburned_loss_design NOTNULL AND NEW.f_boiler_efficiency_design NOTNULL AND NEW.f_water_enthalpy_design NOTNULL AND NEW.f_saturated_water_enthalpy_design NOTNULL AND NEW.f_steam_enthalpy_design NOTNULL AND NEW.f_steam_flow_design NOTNULL AND NEW.c_base_heat_received_user_design NOTNULL THEN
     update biomasschp_boiler_calculation set 

     f_calculation_consumption_design=(NEW.f_steam_flow_design*1000/(NEW.f_boiler_efficiency_design/100)*((NEW.f_steam_enthalpy_design-NEW.f_water_enthalpy_design)+(NEW.f_blowdown_rate_design/100)*(NEW.f_saturated_water_enthalpy_design-NEW.f_water_enthalpy_design))/NEW.c_base_heat_received_user_design)*(1-(NEW.f_unburned_loss_design/100))
     where plan_id=NEW.plan_id;

  END IF;
----------------------实现字段d_total:灰渣总量,的计算9-----------------------------------
  IF OLD.c_base_heat_received_user_design != NEW.c_base_heat_received_user_design OR OLD.f_steam_flow_design != NEW.f_steam_flow_design OR OLD.f_steam_enthalpy_design != NEW.f_steam_enthalpy_design OR OLD.f_saturated_water_enthalpy_design != NEW.f_saturated_water_enthalpy_design OR OLD.f_water_enthalpy_design != NEW.f_water_enthalpy_design OR OLD.f_boiler_efficiency_design != NEW.f_boiler_efficiency_design OR OLD.f_unburned_loss_design != NEW.f_unburned_loss_design OR OLD.f_blowdown_rate_design != NEW.f_blowdown_rate_design OR OLD.c_ash_content_received_design != NEW.c_ash_content_received_design THEN
     update biomasschp_boiler_calculation set 

     d_total_design=(NEW.f_steam_flow_design*1000/(NEW.f_boiler_efficiency_design/100)*((NEW.f_steam_enthalpy_design-NEW.f_water_enthalpy_design)+(NEW.f_blowdown_rate_design/100)*(NEW.f_saturated_water_enthalpy_design-NEW.f_water_enthalpy_design))/NEW.c_base_heat_received_user_design)*(NEW.c_ash_content_received_design/100+NEW.c_base_heat_received_user_design*(NEW.f_unburned_loss_design/100)/3387000)
     where plan_id=NEW.plan_id;

  ELSIF (OLD.f_blowdown_rate_design ISNULL OR OLD.f_unburned_loss_design ISNULL OR OLD.f_boiler_efficiency_design ISNULL OR OLD.f_water_enthalpy_design ISNULL OR OLD.f_saturated_water_enthalpy_design ISNULL OR OLD.f_steam_enthalpy_design ISNULL OR OLD.f_steam_flow_design ISNULL OR OLD.c_base_heat_received_user_design ISNULL OR OLD.c_ash_content_received_design ISNULL) AND NEW.f_blowdown_rate_design NOTNULL AND NEW.f_unburned_loss_design NOTNULL AND NEW.f_boiler_efficiency_design NOTNULL AND NEW.f_water_enthalpy_design NOTNULL AND NEW.f_saturated_water_enthalpy_design NOTNULL AND NEW.f_steam_enthalpy_design NOTNULL AND NEW.f_steam_flow_design NOTNULL AND NEW.c_base_heat_received_user_design NOTNULL AND NEW.c_ash_content_received_design NOTNULL THEN
     update biomasschp_boiler_calculation set 

     d_total_design=(NEW.f_steam_flow_design*1000/(NEW.f_boiler_efficiency_design/100)*((NEW.f_steam_enthalpy_design-NEW.f_water_enthalpy_design)+(NEW.f_blowdown_rate_design/100)*(NEW.f_saturated_water_enthalpy_design-NEW.f_water_enthalpy_design))/NEW.c_base_heat_received_user_design)*(NEW.c_ash_content_received_design/100+NEW.c_base_heat_received_user_design*(NEW.f_unburned_loss_design/100)/3387000)
     where plan_id=NEW.plan_id;

  END IF;
----------------------实现字段d_dust_share:底渣份额,的计算10-----------------------------------
  IF OLD.d_ash_share_design != NEW.d_ash_share_design THEN
     update biomasschp_boiler_calculation set 

     d_dust_share_design=1-NEW.d_ash_share_design
     where plan_id=NEW.plan_id;

  ELSIF (OLD.d_ash_share_design ISNULL) AND NEW.d_ash_share_design NOTNULL THEN
     update biomasschp_boiler_calculation set 

     d_dust_share_design=1-NEW.d_ash_share_design
     where plan_id=NEW.plan_id;

  END IF;
----------------------实现字段d_ash_total:灰量,的计算11-----------------------------------
  IF OLD.c_base_heat_received_user_design != NEW.c_base_heat_received_user_design OR OLD.f_steam_flow_design != NEW.f_steam_flow_design OR OLD.f_steam_enthalpy_design != NEW.f_steam_enthalpy_design OR OLD.f_saturated_water_enthalpy_design != NEW.f_saturated_water_enthalpy_design OR OLD.f_water_enthalpy_design != NEW.f_water_enthalpy_design OR OLD.f_boiler_efficiency_design != NEW.f_boiler_efficiency_design OR OLD.f_unburned_loss_design != NEW.f_unburned_loss_design OR OLD.f_blowdown_rate_design != NEW.f_blowdown_rate_design OR OLD.d_boiler_total_design != NEW.d_boiler_total_design OR OLD.d_ash_share_design != NEW.d_ash_share_design OR OLD.c_ash_content_received_design != NEW.c_ash_content_received_design THEN
     update biomasschp_boiler_calculation set 

     d_ash_total_design=(((NEW.f_steam_flow_design*1000/(NEW.f_boiler_efficiency_design/100)*((NEW.f_steam_enthalpy_design-NEW.f_water_enthalpy_design)+(NEW.f_blowdown_rate_design/100)*(NEW.f_saturated_water_enthalpy_design-NEW.f_water_enthalpy_design))/NEW.c_base_heat_received_user_design)*(NEW.c_ash_content_received_design/100+NEW.c_base_heat_received_user_design*(NEW.f_unburned_loss_design/100)/3387000))+NEW.d_boiler_total_design)*NEW.d_ash_share_design/1000
     where plan_id=NEW.plan_id;

  ELSIF (OLD.d_ash_share_design ISNULL OR OLD.d_boiler_total_design ISNULL OR OLD.f_blowdown_rate_design ISNULL OR OLD.f_unburned_loss_design ISNULL OR OLD.f_boiler_efficiency_design ISNULL OR OLD.f_water_enthalpy_design ISNULL OR OLD.f_saturated_water_enthalpy_design ISNULL OR OLD.f_steam_enthalpy_design ISNULL OR OLD.f_steam_flow_design ISNULL OR OLD.c_base_heat_received_user_design ISNULL OR OLD.c_ash_content_received_design ISNULL) AND NEW.d_ash_share_design NOTNULL AND NEW.d_boiler_total_design NOTNULL AND NEW.f_blowdown_rate_design NOTNULL AND NEW.f_unburned_loss_design NOTNULL AND NEW.f_boiler_efficiency_design NOTNULL AND NEW.f_water_enthalpy_design NOTNULL AND NEW.f_saturated_water_enthalpy_design NOTNULL AND NEW.f_steam_enthalpy_design NOTNULL AND NEW.f_steam_flow_design NOTNULL AND NEW.c_base_heat_received_user_design NOTNULL AND NEW.c_ash_content_received_design NOTNULL THEN
     update biomasschp_boiler_calculation set 

     d_ash_total_design=(((NEW.f_steam_flow_design*1000/(NEW.f_boiler_efficiency_design/100)*((NEW.f_steam_enthalpy_design-NEW.f_water_enthalpy_design)+(NEW.f_blowdown_rate_design/100)*(NEW.f_saturated_water_enthalpy_design-NEW.f_water_enthalpy_design))/NEW.c_base_heat_received_user_design)*(NEW.c_ash_content_received_design/100+NEW.c_base_heat_received_user_design*(NEW.f_unburned_loss_design/100)/3387000))+NEW.d_boiler_total_design)*NEW.d_ash_share_design/1000
     where plan_id=NEW.plan_id;

  END IF;
----------------------实现字段d_dust_total:渣量,的计算12-----------------------------------
  IF OLD.c_base_heat_received_user_design != NEW.c_base_heat_received_user_design OR OLD.f_steam_flow_design != NEW.f_steam_flow_design OR OLD.f_steam_enthalpy_design != NEW.f_steam_enthalpy_design OR OLD.f_saturated_water_enthalpy_design != NEW.f_saturated_water_enthalpy_design OR OLD.f_water_enthalpy_design != NEW.f_water_enthalpy_design OR OLD.f_boiler_efficiency_design != NEW.f_boiler_efficiency_design OR OLD.f_unburned_loss_design != NEW.f_unburned_loss_design OR OLD.f_blowdown_rate_design != NEW.f_blowdown_rate_design OR OLD.d_boiler_total_design != NEW.d_boiler_total_design OR OLD.d_ash_share_design != NEW.d_ash_share_design OR OLD.c_ash_content_received_design != NEW.c_ash_content_received_design THEN
     update biomasschp_boiler_calculation set 

     d_dust_total_design=(((NEW.f_steam_flow_design*1000/(NEW.f_boiler_efficiency_design/100)*((NEW.f_steam_enthalpy_design-NEW.f_water_enthalpy_design)+(NEW.f_blowdown_rate_design/100)*(NEW.f_saturated_water_enthalpy_design-NEW.f_water_enthalpy_design))/NEW.c_base_heat_received_user_design)*(NEW.c_ash_content_received_design/100+NEW.c_base_heat_received_user_design*(NEW.f_unburned_loss_design/100)/3387000))+NEW.d_boiler_total_design)*(1-NEW.d_ash_share_design)/1000
     where plan_id=NEW.plan_id;

  ELSIF (OLD.d_ash_share_design ISNULL OR OLD.d_boiler_total_design ISNULL OR OLD.f_blowdown_rate_design ISNULL OR OLD.f_unburned_loss_design ISNULL OR OLD.f_boiler_efficiency_design ISNULL OR OLD.f_water_enthalpy_design ISNULL OR OLD.f_saturated_water_enthalpy_design ISNULL OR OLD.f_steam_enthalpy_design ISNULL OR OLD.f_steam_flow_design ISNULL OR OLD.c_base_heat_received_user_design ISNULL OR OLD.c_ash_content_received_design ISNULL) AND NEW.d_ash_share_design NOTNULL AND NEW.d_boiler_total_design NOTNULL AND NEW.f_blowdown_rate_design NOTNULL AND NEW.f_unburned_loss_design NOTNULL AND NEW.f_boiler_efficiency_design NOTNULL AND NEW.f_water_enthalpy_design NOTNULL AND NEW.f_saturated_water_enthalpy_design NOTNULL AND NEW.f_steam_enthalpy_design NOTNULL AND NEW.f_steam_flow_design NOTNULL AND NEW.c_base_heat_received_user_design NOTNULL AND NEW.c_ash_content_received_design NOTNULL THEN
     update biomasschp_boiler_calculation set 

     d_dust_total_design=(((NEW.f_steam_flow_design*1000/(NEW.f_boiler_efficiency_design/100)*((NEW.f_steam_enthalpy_design-NEW.f_water_enthalpy_design)+(NEW.f_blowdown_rate_design/100)*(NEW.f_saturated_water_enthalpy_design-NEW.f_water_enthalpy_design))/NEW.c_base_heat_received_user_design)*(NEW.c_ash_content_received_design/100+NEW.c_base_heat_received_user_design*(NEW.f_unburned_loss_design/100)/3387000))+NEW.d_boiler_total_design)*(1-NEW.d_ash_share_design)/1000
     where plan_id=NEW.plan_id;

  END IF;
----------------------实现字段a_air_volumn:理论干空气量,的计算13-----------------------------------
  IF OLD.c_carbon_content_received_design != NEW.c_carbon_content_received_design OR OLD.c_hydrogen_content_received_design != NEW.c_hydrogen_content_received_design OR OLD.c_oxygen_content_received_design != NEW.c_oxygen_content_received_design OR OLD.c_sulfur_content_received_design != NEW.c_sulfur_content_received_design THEN
     update biomasschp_boiler_calculation set 

     a_air_volumn_design=0.0889*NEW.c_carbon_content_received_design+0.265*NEW.c_hydrogen_content_received_design-0.0333*(NEW.c_oxygen_content_received_design-NEW.c_sulfur_content_received_design)
     where plan_id=NEW.plan_id;

  ELSIF (OLD.c_sulfur_content_received_design ISNULL OR OLD.c_oxygen_content_received_design ISNULL OR OLD.c_hydrogen_content_received_design ISNULL OR OLD.c_carbon_content_received_design ISNULL) AND NEW.c_sulfur_content_received_design NOTNULL AND NEW.c_oxygen_content_received_design NOTNULL AND NEW.c_hydrogen_content_received_design NOTNULL AND NEW.c_carbon_content_received_design NOTNULL THEN
     update biomasschp_boiler_calculation set 

     a_air_volumn_design=0.0889*NEW.c_carbon_content_received_design+0.265*NEW.c_hydrogen_content_received_design-0.0333*(NEW.c_oxygen_content_received_design-NEW.c_sulfur_content_received_design)
