  ELSIF (OLD.s_input_smoke ISNULL OR OLD.s_fuel_so2 ISNULL OR OLD.s_feed_consumption ISNULL OR OLD.s_sulfur_content_received ISNULL) AND NEW.s_input_smoke NOTNULL AND NEW.s_fuel_so2 NOTNULL AND NEW.s_feed_consumption NOTNULL AND NEW.s_sulfur_content_received NOTNULL THEN
