     where plan_id=NEW.plan_id;

  END IF;
----------------------实现字段d_exit_smoke_concentration:除尘器（烟囱）出口烟气浓度（标况）,的计算4-----------------------------------
  IF OLD.d_dust_remove_efficiency != NEW.d_dust_remove_efficiency OR OLD.d_total_ash != NEW.d_total_ash OR OLD.d_flyash_fraction != NEW.d_flyash_fraction OR OLD.d_standard_smoke_flow != NEW.d_standard_smoke_flow THEN
     update biomasschp_das_remove set 

     d_exit_smoke_concentration=((NEW.d_flyash_fraction*NEW.d_total_ash)*1000000/NEW.d_standard_smoke_flow)*(1-NEW.d_dust_remove_efficiency/100)
     where plan_id=NEW.plan_id;

  ELSIF (OLD.d_dust_remove_efficiency ISNULL OR OLD.d_standard_smoke_flow ISNULL OR OLD.d_flyash_fraction ISNULL OR OLD.d_total_ash ISNULL) AND NEW.d_dust_remove_efficiency NOTNULL AND NEW.d_standard_smoke_flow NOTNULL AND NEW.d_flyash_fraction NOTNULL AND NEW.d_total_ash NOTNULL THEN
     update biomasschp_das_remove set 

     d_exit_smoke_concentration=((NEW.d_flyash_fraction*NEW.d_total_ash)*1000000/NEW.d_standard_smoke_flow)*(1-NEW.d_dust_remove_efficiency/100)
     where plan_id=NEW.plan_id;

  END IF;
----------------------实现字段d_exit_smoke_flow:除尘器（烟囱）出口烟气飞灰量（标况）,的计算5-----------------------------------
  IF OLD.d_dust_remove_efficiency != NEW.d_dust_remove_efficiency OR OLD.d_total_ash != NEW.d_total_ash OR OLD.d_flyash_fraction != NEW.d_flyash_fraction THEN
