     d_dust_wiper_flow=(NEW.d_flyash_fraction*NEW.d_total_ash)*NEW.d_dust_remove_efficiency/100
     where plan_id=NEW.plan_id;

  ELSIF (OLD.d_dust_remove_efficiency ISNULL OR OLD.d_flyash_fraction ISNULL OR OLD.d_total_ash ISNULL) AND NEW.d_dust_remove_efficiency NOTNULL AND NEW.d_flyash_fraction NOTNULL AND NEW.d_total_ash NOTNULL THEN
     update biomasschp_das_remove set 

