----------------------实现字段d_tun_exit_smoke_concentration:烟囱出口烟气浓度（实态）,的计算7-----------------------------------
  IF OLD.d_dust_remove_efficiency != NEW.d_dust_remove_efficiency OR OLD.d_entry_smoke_volume != NEW.d_entry_smoke_volume OR OLD.d_total_ash != NEW.d_total_ash OR OLD.d_flyash_fraction != NEW.d_flyash_fraction THEN
     update biomasschp_das_remove set 

     d_tun_exit_smoke_concentration=(NEW.d_flyash_fraction*NEW.d_total_ash)*(1-NEW.d_dust_remove_efficiency/100)*1000000/NEW.d_entry_smoke_volume
     where plan_id=NEW.plan_id;

  ELSIF (OLD.d_entry_smoke_volume ISNULL OR OLD.d_dust_remove_efficiency ISNULL OR OLD.d_flyash_fraction ISNULL OR OLD.d_total_ash ISNULL) AND NEW.d_entry_smoke_volume NOTNULL AND NEW.d_dust_remove_efficiency NOTNULL AND NEW.d_flyash_fraction NOTNULL AND NEW.d_total_ash NOTNULL THEN
     update biomasschp_das_remove set 

