     where plan_id=NEW.plan_id;

  END IF;
----------------------实现字段a_remove_output:除灰系统出力,的计算8-----------------------------------
  IF OLD.d_dust_remove_efficiency != NEW.d_dust_remove_efficiency OR OLD.d_total_ash != NEW.d_total_ash OR OLD.d_flyash_fraction != NEW.d_flyash_fraction THEN
     update biomasschp_das_remove set 

     a_remove_output=((NEW.d_flyash_fraction*NEW.d_total_ash)*NEW.d_dust_remove_efficiency/100)*2.5/1000
     where plan_id=NEW.plan_id;

  ELSIF (OLD.d_dust_remove_efficiency ISNULL OR OLD.d_flyash_fraction ISNULL OR OLD.d_total_ash ISNULL) AND NEW.d_dust_remove_efficiency NOTNULL AND NEW.d_flyash_fraction NOTNULL AND NEW.d_total_ash NOTNULL THEN
     update biomasschp_das_remove set 

