     update biomasschp_das_remove set 

     a_volumn=((NEW.d_flyash_fraction*NEW.d_total_ash)*NEW.d_dust_remove_efficiency/100)/1000*NEW.a_storage_time*24/NEW.a_bulk_density/NEW.a_fullness
     where plan_id=NEW.plan_id;

  ELSIF (OLD.a_storage_time ISNULL OR OLD.a_fullness ISNULL OR OLD.a_bulk_density ISNULL OR OLD.d_dust_remove_efficiency ISNULL OR OLD.d_flyash_fraction ISNULL OR OLD.d_total_ash ISNULL) AND NEW.a_storage_time NOTNULL AND NEW.a_fullness NOTNULL AND NEW.a_bulk_density NOTNULL AND NEW.d_dust_remove_efficiency NOTNULL AND NEW.d_flyash_fraction NOTNULL AND NEW.d_total_ash NOTNULL THEN
     update biomasschp_das_remove set 

     a_volumn=((NEW.d_flyash_fraction*NEW.d_total_ash)*NEW.d_dust_remove_efficiency/100)/1000*NEW.a_storage_time*24/NEW.a_bulk_density/NEW.a_fullness
     where plan_id=NEW.plan_id;

