  ELSIF (OLD.a_diameter ISNULL OR OLD.a_storage_time ISNULL OR OLD.a_fullness ISNULL OR OLD.a_bulk_density ISNULL OR OLD.d_dust_remove_efficiency ISNULL OR OLD.d_flyash_fraction ISNULL OR OLD.d_total_ash ISNULL) AND NEW.a_diameter NOTNULL AND NEW.a_storage_time NOTNULL AND NEW.a_fullness NOTNULL AND NEW.a_bulk_density NOTNULL AND NEW.d_dust_remove_efficiency NOTNULL AND NEW.d_flyash_fraction NOTNULL AND NEW.d_total_ash NOTNULL THEN
