     heat_turbine_flow=(NEW.heat_area*NEW.heat_hot_target/1000)*3.6/NEW.hidden_turbine
     where plan_id=NEW.plan_id;

  END IF;
----------------------实现字段hot_turbine_flow:汽轮机抽汽量,的计算3-----------------------------------
  IF OLD.hot_loss != NEW.hot_loss OR OLD.use_flow != NEW.use_flow OR OLD.steam_supply_rate != NEW.steam_supply_rate THEN
     update biomasschp_heat_supply set 

     hot_turbine_flow=NEW.use_flow*NEW.steam_supply_rate*0.01/(1-NEW.hot_loss*0.01)
     where plan_id=NEW.plan_id;

  ELSIF (OLD.hot_loss ISNULL OR OLD.steam_supply_rate ISNULL OR OLD.use_flow ISNULL) AND NEW.hot_loss NOTNULL AND NEW.steam_supply_rate NOTNULL AND NEW.use_flow NOTNULL THEN
     update biomasschp_heat_supply set 

     hot_turbine_flow=NEW.use_flow*NEW.steam_supply_rate*0.01/(1-NEW.hot_loss*0.01)
     where plan_id=NEW.plan_id;

  END IF;
RETURN NULL;
END;
