CREATE OR REPLACE FUNCTION biomasschp_boiler_calculation_design()
RETURNS TRIGGER AS
$BODY$
BEGIN
----------------------实现字段c_sum:总和,的计算1-----------------------------------
  IF OLD.c_water_content_received_design != NEW.c_water_content_received_design OR OLD.c_carbon_content_received_design != NEW.c_carbon_content_received_design OR OLD.c_hydrogen_content_received_design != NEW.c_hydrogen_content_received_design OR OLD.c_oxygen_content_received_design != NEW.c_oxygen_content_received_design OR OLD.c_nitrogen_content_design != NEW.c_nitrogen_content_design OR OLD.c_sulfur_content_received_design != NEW.c_sulfur_content_received_design OR OLD.c_ash_content_received_design != NEW.c_ash_content_received_design THEN
     update biomasschp_boiler_calculation set 

     c_sum_design=NEW.c_carbon_content_received_design+NEW.c_hydrogen_content_received_design+NEW.c_oxygen_content_received_design+NEW.c_nitrogen_content_design+NEW.c_sulfur_content_received_design+NEW.c_ash_content_received_design+NEW.c_water_content_received_design
     where plan_id=NEW.plan_id;

  ELSIF (OLD.c_water_content_received_design ISNULL OR OLD.c_ash_content_received_design ISNULL OR OLD.c_sulfur_content_received_design ISNULL OR OLD.c_nitrogen_content_design ISNULL OR OLD.c_oxygen_content_received_design ISNULL OR OLD.c_hydrogen_content_received_design ISNULL OR OLD.c_carbon_content_received_design ISNULL) AND NEW.c_water_content_received_design NOTNULL AND NEW.c_ash_content_received_design NOTNULL AND NEW.c_sulfur_content_received_design NOTNULL AND NEW.c_nitrogen_content_design NOTNULL AND NEW.c_oxygen_content_received_design NOTNULL AND NEW.c_hydrogen_content_received_design NOTNULL AND NEW.c_carbon_content_received_design NOTNULL THEN
     update biomasschp_boiler_calculation set 

     c_sum_design=NEW.c_carbon_content_received_design+NEW.c_hydrogen_content_received_design+NEW.c_oxygen_content_received_design+NEW.c_nitrogen_content_design+NEW.c_sulfur_content_received_design+NEW.c_ash_content_received_design+NEW.c_water_content_received_design
     where plan_id=NEW.plan_id;

  END IF;
----------------------实现字段c_daf:干燥无灰基挥发分,的计算2-----------------------------------
  IF OLD.c_water_content_received_design != NEW.c_water_content_received_design OR OLD.c_base_volatile_obtained_design != NEW.c_base_volatile_obtained_design OR OLD.c_carbon_content_received_design != NEW.c_carbon_content_received_design OR OLD.c_hydrogen_content_received_design != NEW.c_hydrogen_content_received_design OR OLD.c_oxygen_content_received_design != NEW.c_oxygen_content_received_design OR OLD.c_nitrogen_content_design != NEW.c_nitrogen_content_design OR OLD.c_sulfur_content_received_design != NEW.c_sulfur_content_received_design OR OLD.c_ash_content_received_design != NEW.c_ash_content_received_design THEN
     update biomasschp_boiler_calculation set 

     c_daf_design=NEW.c_base_volatile_obtained_design/((NEW.c_carbon_content_received_design+NEW.c_hydrogen_content_received_design+NEW.c_oxygen_content_received_design+NEW.c_nitrogen_content_design+NEW.c_sulfur_content_received_design+NEW.c_ash_content_received_design+NEW.c_water_content_received_design)-NEW.c_water_content_received_design-NEW.c_ash_content_received_design)*100
     where plan_id=NEW.plan_id;

  ELSIF (OLD.c_base_volatile_obtained_design ISNULL OR OLD.c_water_content_received_design ISNULL OR OLD.c_ash_content_received_design ISNULL OR OLD.c_sulfur_content_received_design ISNULL OR OLD.c_nitrogen_content_design ISNULL OR OLD.c_oxygen_content_received_design ISNULL OR OLD.c_hydrogen_content_received_design ISNULL OR OLD.c_carbon_content_received_design ISNULL) AND NEW.c_base_volatile_obtained_design NOTNULL AND NEW.c_water_content_received_design NOTNULL AND NEW.c_ash_content_received_design NOTNULL AND NEW.c_sulfur_content_received_design NOTNULL AND NEW.c_nitrogen_content_design NOTNULL AND NEW.c_oxygen_content_received_design NOTNULL AND NEW.c_hydrogen_content_received_design NOTNULL AND NEW.c_carbon_content_received_design NOTNULL THEN
     update biomasschp_boiler_calculation set 

     c_daf_design=NEW.c_base_volatile_obtained_design/((NEW.c_carbon_content_received_design+NEW.c_hydrogen_content_received_design+NEW.c_oxygen_content_received_design+NEW.c_nitrogen_content_design+NEW.c_sulfur_content_received_design+NEW.c_ash_content_received_design+NEW.c_water_content_received_design)-NEW.c_water_content_received_design-NEW.c_ash_content_received_design)*100
     where plan_id=NEW.plan_id;

  END IF;
----------------------实现字段c_base_heat_received_calculation:收到基低位发热量计算得到,的计算3-----------------------------------
  IF OLD.c_base_heat_received_user_design != NEW.c_base_heat_received_user_design THEN
     update biomasschp_boiler_calculation set 

     c_base_heat_received_calculation_design=NEW.c_base_heat_received_user_design/4.1868
     where plan_id=NEW.plan_id;

  ELSIF (OLD.c_base_heat_received_user_design ISNULL) AND NEW.c_base_heat_received_user_design NOTNULL THEN
     update biomasschp_boiler_calculation set 

     c_base_heat_received_calculation_design=NEW.c_base_heat_received_user_design/4.1868
     where plan_id=NEW.plan_id;

  END IF;
----------------------实现字段c_low_calorific_value_estimation:低位发热量估算,的计算4-----------------------------------
  IF OLD.c_water_content_received_design != NEW.c_water_content_received_design OR OLD.c_carbon_content_received_design != NEW.c_carbon_content_received_design OR OLD.c_hydrogen_content_received_design != NEW.c_hydrogen_content_received_design OR OLD.c_oxygen_content_received_design != NEW.c_oxygen_content_received_design OR OLD.c_sulfur_content_received_design != NEW.c_sulfur_content_received_design THEN
     update biomasschp_boiler_calculation set 

     c_low_calorific_value_estimation_design=339*NEW.c_carbon_content_received_design+1030*NEW.c_hydrogen_content_received_design-109*(NEW.c_oxygen_content_received_design-NEW.c_sulfur_content_received_design)-25*NEW.c_water_content_received_design
     where plan_id=NEW.plan_id;

  ELSIF (OLD.c_water_content_received_design ISNULL OR OLD.c_sulfur_content_received_design ISNULL OR OLD.c_oxygen_content_received_design ISNULL OR OLD.c_hydrogen_content_received_design ISNULL OR OLD.c_carbon_content_received_design ISNULL) AND NEW.c_water_content_received_design NOTNULL AND NEW.c_sulfur_content_received_design NOTNULL AND NEW.c_oxygen_content_received_design NOTNULL AND NEW.c_hydrogen_content_received_design NOTNULL AND NEW.c_carbon_content_received_design NOTNULL THEN
     update biomasschp_boiler_calculation set 

