     where plan_id=NEW.plan_id;

  ELSIF (OLD.f_blowdown_rate_design ISNULL OR OLD.f_unburned_loss_design ISNULL OR OLD.f_boiler_efficiency_design ISNULL OR OLD.f_water_enthalpy_design ISNULL OR OLD.f_saturated_water_enthalpy_design ISNULL OR OLD.f_steam_enthalpy_design ISNULL OR OLD.f_steam_flow_design ISNULL OR OLD.c_base_heat_received_user_design ISNULL OR OLD.c_ash_content_received_design ISNULL) AND NEW.f_blowdown_rate_design NOTNULL AND NEW.f_unburned_loss_design NOTNULL AND NEW.f_boiler_efficiency_design NOTNULL AND NEW.f_water_enthalpy_design NOTNULL AND NEW.f_saturated_water_enthalpy_design NOTNULL AND NEW.f_steam_enthalpy_design NOTNULL AND NEW.f_steam_flow_design NOTNULL AND NEW.c_base_heat_received_user_design NOTNULL AND NEW.c_ash_content_received_design NOTNULL THEN
