BEGIN
----------------------实现字段d_entry_flyash:除尘器入口（锅炉出口）飞灰量,的计算1-----------------------------------
  IF OLD.d_total_ash != NEW.d_total_ash OR OLD.d_flyash_fraction != NEW.d_flyash_fraction THEN
     update biomasschp_das_remove set 

     d_entry_flyash=NEW.d_flyash_fraction*NEW.d_total_ash
     where plan_id=NEW.plan_id;

  ELSIF (OLD.d_flyash_fraction ISNULL OR OLD.d_total_ash ISNULL) AND NEW.d_flyash_fraction NOTNULL AND NEW.d_total_ash NOTNULL THEN
