     d_standard_smoke_concentration=(NEW.d_flyash_fraction*NEW.d_total_ash)*1000000/NEW.d_standard_smoke_flow
     where plan_id=NEW.plan_id;

  ELSIF (OLD.d_standard_smoke_flow ISNULL OR OLD.d_flyash_fraction ISNULL OR OLD.d_total_ash ISNULL) AND NEW.d_standard_smoke_flow NOTNULL AND NEW.d_flyash_fraction NOTNULL AND NEW.d_total_ash NOTNULL THEN
     update biomasschp_das_remove set 

