  ELSIF (OLD.s_diameter ISNULL OR OLD.s_storage_time ISNULL OR OLD.s_fullness ISNULL OR OLD.s_bulk_density ISNULL OR OLD.s_slag_quantity ISNULL) AND NEW.s_diameter NOTNULL AND NEW.s_storage_time NOTNULL AND NEW.s_fullness NOTNULL AND NEW.s_bulk_density NOTNULL AND NEW.s_slag_quantity NOTNULL THEN
     update biomasschp_das_remove set 

     s_height=(NEW.s_slag_quantity*NEW.s_storage_time*24/NEW.s_bulk_density/NEW.s_fullness)/(3.14*(NEW.s_diameter/2)*(NEW.s_diameter/2))
     where plan_id=NEW.plan_id;

  END IF;
RETURN NULL;
END;
